import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './CSS/Menu.css';

const Menu = () => {
    const navigate = useNavigate();

    // Check if user is logged in
    useEffect(() => {
        const token = localStorage.getItem('authToken');
        if (!token) {
            // Redirect to log in if not authenticated
            navigate('/login');
        }
    }, [navigate]);


    return (
        <div>
            <div className="flex menu-background-1">
                <div className="w-1/2">
                    <h1 className="font-sf font-bold text-6xl text-amber-50 menu-text-1">
                        Browse through our cuisine to find the perfect dish for yourself.
                    </h1>
                </div>
                <div className="w-1/2"></div>
            </div>

            <div className="flex menu-background-2">
                <div className="w-1/2">
                    <button
                        className="px-4 py-2 sm:px-6 sm:py-3 bg-yellow-500 text-white text-base sm:text-xl font-semibold rounded-lg shadow-lg hover:bg-yellow-400 transition duration-300">
                        Order Now!
                    </button>
                </div>
                <div className="w-1/2 menu-button-1">
                    <h1 className="font-sf font-bold text-6xl text-amber-50 menu-text-1">
                        Experience Authentic Indian Flavors â€“ Order Now!
                    </h1>
                </div>
            </div>


        </div>


    );
};

export default Menu;
