import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './CSS/Menu.css';

const Menu = () => {
    const navigate = useNavigate();

    // Check if user is logged in
    useEffect(() => {
        const token = localStorage.getItem('authToken');
        if (!token) {
            // Redirect to log in if not authenticated
            navigate('/login');
        }
    }, [navigate]);


    return (
        <div>
            <div className="flex items-center justify-center menu-background-1 h-screen">
                <h1 className="font-sf font-bold text-6xl text-amber-50 menu-text-1 text-center">
                    Browse through our cuisine to find the perfect dish for yourself.
                </h1>
            </div>


            <a href="#"
               className="flex flex-col items-center bg-white border border-gray-200 rounded-lg shadow md:flex-row md:max-w-xl hover:bg-gray-100 dark:border-gray-700 dark:bg-gray-800 dark:hover:bg-gray-700 mt-4">
                <img className="object-cover w-full rounded-t-lg h-96 md:h-auto md:w-48 md:rounded-none md:rounded-s-lg"
                     src="/docs/images/blog/image-4.jpg" alt=""/>
                <div className="flex flex-col justify-between p-4 leading-normal">
                    <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Noteworthy
                        technology acquisitions 2021</h5>
                    <p className="mb-3 font-normal text-gray-700 dark:text-gray-400">Here are the biggest enterprise
                        technology acquisitions of 2021 so far, in reverse chronological order.</p>
                </div>
            </a>


            <div className="flex menu-background-2">
                <div className="w-1/2 menu-button-1">
                    <button
                        className="px-4 py-2 sm:px-6 sm:py-3 bg-yellow-500 text-white text-base sm:text-xl font-semibold rounded-lg shadow-lg hover:bg-yellow-400 transition duration-300">
                        Order Now!
                    </button>
                </div>
                <div className="w-1/2">
                    <h1 className="font-sf font-bold text-5xl text-amber-50 menu-text-2">
                        Experience Authentic Indian Flavors Order Now!
                    </h1>
                </div>
            </div>


            <div className="flex menu-background-3">
                <div className="w-1/2">
                    <h1 className="font-sf font-bold text-6xl text-amber-50 menu-text-3">
                        Craving Italian? Order your favorites today and bring Italy to your door!
                    </h1>
                </div>
                <div className="w-1/2">
                    <button
                        className="px-4 py-2 sm:px-6 sm:py-3 bg-yellow-500 text-white text-base sm:text-xl font-semibold rounded-lg shadow-lg hover:bg-yellow-400 transition duration-300">
                        Order Now!
                    </button>
                </div>
            </div>


        </div>


    );
};

export default Menu;
