import React, { useEffect, useState } from "react";

const Menu = () => {
    // State to store menu items
    const [menuItems, setMenuItems] = useState([]);

    // State for loading state
    const [loading, setLoading] = useState(true);

    // State for error handling
    const [error, setError] = useState(null);

    // State for current page and items per page
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 10;

    // State to store quantities of each product
    const [quantities, setQuantities] = useState({});

    // Fetch menu items from API
    useEffect(() => {
        const fetchMenuItems = async () => {
            try {
                // Fetch menu items from the API
                const response = await fetch('http://localhost:5001/menu');
                if (!response.ok) {
                    throw new Error('Failed to fetch menu items');
                }
                const data = await response.json();
                setMenuItems(data); // Update state with fetched data
            } catch (error) {
                setError(error.message); // Set error if fetching fails
            } finally {
                setLoading(false); // Set loading to false once fetching is complete
            }
        };

        fetchMenuItems();
    }, []);

    // Calculate current page items
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = menuItems.slice(indexOfFirstItem, indexOfLastItem);

    // Handle page navigation
    const nextPage = () => {
        if (currentPage < Math.ceil(menuItems.length / itemsPerPage)) {
            setCurrentPage(currentPage + 1);
        }
    };

    const prevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    // Increment quantity of a specific product
    const incrementQuantity = (productId) => {
        setQuantities((prevQuantities) => ({
            ...prevQuantities,
            [productId]: (prevQuantities[productId] || 0) + 1,
        }));
    };

    // Decrement quantity of a specific product
    const decrementQuantity = (productId) => {
        setQuantities((prevQuantities) => ({
            ...prevQuantities,
            [productId]: Math.max((prevQuantities[productId] || 0) - 1, 0),
        }));
    };

    // Handle Add to Cart for a specific product
    const handleAddToCart = (productId) => {
        setQuantities((prevQuantities) => ({
            ...prevQuantities,
            [productId]: 1, // Set quantity to 1 when "Add to Cart" is clicked
        }));
    };

    if (loading) {
        return <div>Loading...</div>; // Show loading message while fetching data
    }

    if (error) {
        return <div>Error: {error}</div>; // Show error message if there's an error
    }

    return (
        <div className="menu-container">
            {/* Search Bar */}
            <div className="search-bar px-4 sm:px-6 md:px-8 lg:px-16 xl:px-24">
                <form className="max-w-full md:max-w-lg lg:max-w-2xl mx-auto mt-4 lg:mt-8">
                    <label
                        htmlFor="default-search"
                        className="mb-2 text-sm font-medium text-gray-900 sr-only dark:text-white"
                    >
                        Search
                    </label>
                    <div className="relative">
                        <div className="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
                            <svg
                                className="w-4 h-4 text-gray-500 dark:text-gray-400"
                                aria-hidden="true"
                                xmlns="http://www.w3.org/2000/svg"
                                fill="none"
                                viewBox="0 0 20 20"
                            >
                                <path
                                    stroke="currentColor"
                                    strokeLinecap="round"
                                    strokeLinejoin="round"
                                    strokeWidth="2"
                                    d="m19 19-4-4m0-7A7 7 0 1 1 1 8a7 7 0 0 1 14 0Z"
                                />
                            </svg>
                        </div>
                        <input
                            type="search"
                            id="default-search"
                            className="block w-full p-4 ps-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-50 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                            placeholder="Search products..."
                            required
                        />
                        <button
                            type="submit"
                            className="text-white absolute end-2.5 bottom-2.5 bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
                        >
                            Search
                        </button>
                    </div>
                </form>
            </div>

            {/* Product Cards */}
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-2 lg:grid-cols-5 xl:grid-cols-5 gap-6 m-6">
                {currentItems.map((product) => (
                    <div
                        key={product.id}
                        className="relative flex flex-col overflow-hidden rounded-lg border border-gray-100 bg-white shadow-md"
                    >
                        <a
                            className="relative mx-3 mt-3 flex h-60 overflow-hidden rounded-xl"
                            href="#"
                        >
                            <img
                                className="object-cover w-full h-full"
                                src={product.image}
                                alt={product.name}
                            />
                        </a>
                        <div className="mt-4 px-5 pb-5">
                            <a href="#">
                                <h5 className="text-xl tracking-tight text-slate-900">
                                    {product.name}
                                </h5>
                            </a>
                            <div className="mt-2 mb-5 flex items-center justify-between">
                                <p>
                                    <span className="text-2xl font-bold text-slate-900">
                                        {product.price}
                                    </span>
                                </p>
                                <div className="flex items-center">
                                    {[...Array(Math.round(product.rating))].map((_, index) => (
                                        <svg
                                            key={index}
                                            aria-hidden="true"
                                            className="h-5 w-5 text-yellow-300"
                                            fill="currentColor"
                                            viewBox="0 0 20 20"
                                            xmlns="http://www.w3.org/2000/svg"
                                        >
                                            <path
                                                d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"></path>
                                        </svg>
                                    ))}
                                    <span className="ml-2 rounded bg-yellow-200 px-1.5 py-0.25 text-[10px] font-medium">
                                        {product.rating}
                                    </span>
                                </div>
                            </div>

                            {/* Add to Cart / Quantity */}
                            {quantities[product.id] === 0 ? (
                                <button
                                    onClick={() => handleAddToCart(product.id)}
                                    className="flex items-center justify-center rounded-md bg-slate-900 px-5 py-2.5 text-center text-sm font-medium text-white hover:bg-gray-700 focus:outline-none focus:ring-4 focus:ring-blue-300"
                                >
                                    <svg
                                        xmlns="http://www.w3.org/2000/svg"
                                        className="mr-2 h-6 w-6"
                                        fill="none"
                                        viewBox="0 0 24 24"
                                        stroke="currentColor"
                                        strokeWidth="2"
                                    >
                                        <path
                                            strokeLinecap="round"
                                            strokeLinejoin="round"
                                            d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z"
                                        />
                                    </svg>
                                    Add to cart
                                </button>
                            ) : (
                                <div className="flex items-center space-x-2">
                                    <button
                                        onClick={() => decrementQuantity(product.id)}
                                        className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
                                    >
                                        -
                                    </button>
                                    <span className="text-lg">{quantities[product.id]}</span>
                                    <button
                                        onClick={() => incrementQuantity(product.id)}
                                        className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
                                    >
                                        +
                                    </button>
                                </div>
                            )}
                        </div>
                    </div>
                ))}
            </div>

            {/* Pagination */}
            <div className="flex justify-between mt-8">
                <button
                    onClick={prevPage}
                    className="px-4 py-2 text-sm font-medium text-white bg-gray-900 rounded-lg hover:bg-gray-700"
                >
                    Previous
                </button>
                <button
                    onClick={nextPage}
                    className="px-4 py-2 text-sm font-medium text-white bg-gray-900 rounded-lg hover:bg-gray-700"
                >
                    Next
                </button>
            </div>
        </div>
    );
};

export default Menu;
