import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './CSS/Menu.css';

const Menu = () => {
    const navigate = useNavigate();

    // Check if user is logged in
    useEffect(() => {
        const token = localStorage.getItem('authToken');
        if (!token) {
            // Redirect to log in if not authenticated
            navigate('/login');
        }
    }, [navigate]);

    return (
        <div>
            <div className="flex items-center justify-center menu-background-1 h-screen">
                <h1 className="font-sf font-bold text-6xl text-amber-50 menu-text-1 text-center">
                    Browse through our cuisine to find the perfect dish for yourself.
                </h1>
            </div>

            <div className="flex items-center justify-center bg-amber-50">

                <div className="w-[40%] mt-10 mb-10">
                    <img src="/images/menubg.jpg" alt="menubg" className="w-full h-auto object-cover rounded-lg"/>
                </div>

                <div className="w-1/2 flex flex-col items-start justify-center space-y-6 px-8 ml-10">
                    <h1 className="font-sf font-bold text-5xl text-black">
                        Experience Authentic Indian Flavors Order Now!
                    </h1>
                    <button
                        className="mx-auto px-6 py-3 bg-yellow-500 text-white text-lg font-semibold rounded-lg shadow-lg hover:bg-yellow-400 transition duration-300">
                        Order Now!
                    </button>
                </div>
            </div>

            <div className="flex menu-background-2">
                <div className="w-1/2 menu-button-1">
                    <button
                        className="px-4 py-2 sm:px-6 sm:py-3 bg-yellow-500 text-white text-base sm:text-xl font-semibold rounded-lg shadow-lg hover:bg-yellow-400 transition duration-300">
                        Order Now!
                    </button>
                </div>
                <div className="w-1/2">
                    <h1 className="font-sf font-bold text-5xl text-amber-50 menu-text-2">
                        Experience Authentic Indian Flavors Order Now!
                    </h1>
                </div>
            </div>


            <div className="flex menu-background-3">
                <div className="w-1/2">
                    <h1 className="font-sf font-bold text-6xl text-amber-50 menu-text-3">
                        Craving Italian? Order your favorites today and bring Italy to your door!
                    </h1>
                </div>
                <div className="w-1/2">
                    <button
                        className="px-4 py-2 sm:px-6 sm:py-3 bg-yellow-500 text-white text-base sm:text-xl font-semibold rounded-lg shadow-lg hover:bg-yellow-400 transition duration-300">
                        Order Now!
                    </button>
                </div>
            </div>


        </div>


    );
};

export default Menu;
