import React, { useEffect, useState } from "react";

const Menu = () => {
    // State to store menu items
    const [menuItems, setMenuItems] = useState([]);

    // State for loading state
    const [loading, setLoading] = useState(true);

    // State for error handling
    const [error, setError] = useState(null);

    // State for current page and items per page
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 10;

    // Fetch menu items from API
    useEffect(() => {
        const fetchMenuItems = async () => {
            try {
                const response = await fetch("http://localhost:5001/menu");
                if (!response.ok) {
                    throw new Error("Failed to fetch menu items");
                }
                const data = await response.json();
                setMenuItems(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchMenuItems();
    }, []);

    // Calculate current page items
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = menuItems.slice(indexOfFirstItem, indexOfLastItem);

    // Handle page navigation
    const nextPage = () => {
        if (currentPage < Math.ceil(menuItems.length / itemsPerPage)) {
            setCurrentPage(currentPage + 1);
        }
    };

    const prevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="menu-container">
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-2 lg:grid-cols-5 xl:grid-cols-5 gap-6 m-6">
                {currentItems.map((product) => (
                    <div
                        key={product.id}
                        className="relative flex flex-col overflow-hidden rounded-lg border border-gray-100 bg-white shadow-md"
                    >
                        <a
                            className="relative mx-3 mt-3 flex h-60 overflow-hidden rounded-xl"
                            href="#"
                        >
                            <img
                                className="object-cover w-full h-full"
                                src={product.image}
                                alt={product.name}
                            />
                        </a>
                        <div className="mt-4 px-5 pb-5">
                            <a href="#">
                                <h5 className="text-xl tracking-tight text-slate-900">
                                    {product.name}
                                </h5>
                            </a>
                            <div className="mt-2 mb-5 flex items-center justify-between">
                                <p>
                                    <span className="text-2xl font-bold text-slate-900">
                                        {product.price}
                                    </span>
                                </p>
                                <div className="flex items-center">
                                    {[...Array(Math.round(product.rating))].map((_, index) => (
                                        <svg
                                            key={index}
                                            aria-hidden="true"
                                            className="h-5 w-5 text-yellow-300"
                                            fill="currentColor"
                                            viewBox="0 0 20 20"
                                            xmlns="http://www.w3.org/2000/svg"
                                        >
                                            <path
                                                d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"></path>
                                        </svg>
                                    ))}
                                    <span className="ml-2 rounded bg-yellow-200 px-1.5 py-0.25 text-[10px] font-medium">
                                        {product.rating}
                                    </span>
                                </div>
                            </div>
                            {/* Quantity Logic */}
                            <div>
                                {product.quantity === 0 ? (
                                    <button
                                        onClick={() =>
                                            setMenuItems((prev) =>
                                                prev.map((item) =>
                                                    item.id === product.id
                                                        ? { ...item, quantity: 1 }
                                                        : item
                                                )
                                            )
                                        }
                                        className="flex items-center justify-center rounded-md bg-slate-900 px-5 py-2.5 text-center text-sm font-medium text-white hover:bg-gray-700 focus:outline-none focus:ring-4 focus:ring-blue-300"
                                    >
                                        Add to cart
                                    </button>
                                ) : (
                                    <div className="flex items-center space-x-2">
                                        <button
                                            onClick={() =>
                                                setMenuItems((prev) =>
                                                    prev.map((item) =>
                                                        item.id === product.id && item.quantity > 0
                                                            ? { ...item, quantity: item.quantity - 1 }
                                                            : item
                                                    )
                                                )
                                            }
                                            className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
                                        >
                                            -
                                        </button>
                                        <span>{product.quantity}</span>
                                        <button
                                            onClick={() =>
                                                setMenuItems((prev) =>
                                                    prev.map((item) =>
                                                        item.id === product.id
                                                            ? { ...item, quantity: item.quantity + 1 }
                                                            : item
                                                    )
                                                )
                                            }
                                            className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
                                        >
                                            +
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Menu;
