import React, { useState } from "react";
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [rememberMe, setRememberMe] = useState(false);
    const [error, setError] = useState(""); // State for error message
    const navigate = useNavigate(); // useNavigate hook for navigation

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!email || !password) {
            setError("Email and password are required.");
            return;
        }

        try {
            const response = await axios.post("http://localhost:5001/login", {
                email,
                password
            }, {
                headers: {
                    'Content-Type': 'application/json',
                }
            });

            if (response.data && response.data.user && response.data.user.id && response.data.authToken) {
                localStorage.setItem('authToken', response.data.authToken);  // Store the JWT token
                localStorage.setItem('userId', response.data.user.userId);  // Store the userId

                // Trigger a re-render after login by updating the local state
                navigate("/menu");  // Redirect to the Menu page after login
            } else {
                setError("Unexpected response format from server.");
            }
        } catch (err) {
            if (err.response) {
                setError("Invalid email or password.");
            } else if (err.request) {
                setError("Server not responding, please try again later.");
            } else {
                setError("An error occurred, please try again.");
            }
        }
    };
    // const handleSubmit = async (e) => {
