import React, { useEffect, useState } from "react";

const Menu = () => {
    const [menuItems, setMenuItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 10;

    // State to manage cart quantities
    const [cart, setCart] = useState({}); // { productId: quantity }

    useEffect(() => {
        const fetchMenuItems = async () => {
            try {
                const response = await fetch('http://localhost:5001/menu');
                if (!response.ok) {
                    throw new Error('Failed to fetch menu items');
                }
                const data = await response.json();
                setMenuItems(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchMenuItems();
    }, []);

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = menuItems.slice(indexOfFirstItem, indexOfLastItem);

    const nextPage = () => {
        if (currentPage < Math.ceil(menuItems.length / itemsPerPage)) {
            setCurrentPage(currentPage + 1);
        }
    };

    const prevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    const incrementQuantity = (productId) => {
        setCart((prevCart) => ({
            ...prevCart,
            [productId]: (prevCart[productId] || 0) + 1,
        }));
    };

    const decrementQuantity = (productId) => {
        setCart((prevCart) => {
            const updatedCart = { ...prevCart };
            if (updatedCart[productId] > 1) {
                updatedCart[productId] -= 1;
            } else {
                delete updatedCart[productId];
            }
            return updatedCart;
        });
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="menu-container">
            {/* Search Bar */}
            <div className="search-bar px-4 sm:px-6 md:px-8 lg:px-16 xl:px-24">
                <form className="max-w-full md:max-w-lg lg:max-w-2xl mx-auto mt-4 lg:mt-8">
                    <label htmlFor="default-search" className="sr-only">Search</label>
                    <div className="relative">
                        <input
                            type="search"
                            id="default-search"
                            className="block w-full p-4 ps-10 text-sm border rounded-lg bg-gray-50"
                            placeholder="Search products..."
                        />
                        <button type="submit" className="absolute right-2.5 bottom-2.5 bg-blue-700 text-white px-4 py-2 rounded-lg">
                            Search
                        </button>
                    </div>
                </form>
            </div>

            {/* Product Cards */}
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-2 lg:grid-cols-5 xl:grid-cols-5 gap-6 m-6">
                {currentItems.map((product) => (
                    <div
                        key={product.id}
                        className="relative flex flex-col overflow-hidden rounded-lg border bg-white shadow-md"
                    >
                        <a className="relative mx-3 mt-3 flex h-60 overflow-hidden rounded-xl" href="#">
                            <img
                                className="object-cover w-full h-full"
                                src={product.image}
                                alt={product.name}
                            />
                        </a>
                        <div className="mt-4 px-5 pb-5">
                            <a href="#">
                                <h5 className="text-xl tracking-tight text-slate-900">
                                    {product.name}
                                </h5>
                            </a>
                            <div className="mt-2 mb-5 flex items-center justify-between">
                                <p>
                                    <span className="text-2xl font-bold text-slate-900">
                                        ${product.price}
                                    </span>
                                </p>
                                <div className="flex items-center">
                                    {[...Array(Math.round(product.rating))].map((_, index) => (
                                        <svg
                                            key={index}
                                            className="h-5 w-5 text-yellow-300"
                                            fill="currentColor"
                                            viewBox="0 0 20 20"
                                        >
                                            <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"></path>
                                        </svg>
                                    ))}
                                    <span className="ml-2 text-[10px] font-medium bg-yellow-200 px-1.5 py-0.5">
                                        {product.rating}
                                    </span>
                                </div>
                            </div>
                            {cart[product.id] > 0 ? (
                                <div className="flex items-center space-x-2">
                                    <button
                                        onClick={() => decrementQuantity(product.id)}
                                        className="px-2 py-1 bg-red-500 text-white rounded"
                                    >
                                        -
                                    </button>
                                    <span>{cart[product.id]}</span>
                                    <button
                                        onClick={() => incrementQuantity(product.id)}
                                        className="px-2 py-1 bg-green-500 text-white rounded"
                                    >
                                        +
                                    </button>
                                </div>
                            ) : (
                                <button
                                    onClick={() => incrementQuantity(product.id)}
                                    className="w-full bg-blue-500 text-white py-2 rounded"
                                >
                                    Add to Cart
                                </button>
                            )}
                        </div>
                    </div>
                ))}
            </div>

            {/* Pagination */}
            <nav className="flex justify-center">
                <ul className="flex items-center space-x-2">
                    <li>
                        <button
                            onClick={prevPage}
                            className="px-4 py-2 bg-gray-300 rounded-l-lg"
                        >
                            Prev
                        </button>
                    </li>
                    <li>
                        <span className="px-4 py-2 bg-gray-200">
                            {currentPage}
                        </span>
                    </li>
                    <li>
                        <button
                            onClick={nextPage}
                            className="px-4 py-2 bg-gray-300 rounded-r-lg"
                        >
                            Next
                        </button>
                    </li>
                </ul>
            </nav>
        </div>
    );
};

export default Menu;
