import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Menu = () => {
    const navigate = useNavigate();

    // Check if user is logged in
    useEffect(() => {
        const token = localStorage.getItem('authToken');
        if (!token) {
            // Redirect to login if not authenticated
            navigate('/login');
        }
    }, [navigate]);

    // Sample data for products
    const products = [
        {
            id: 1,
            name: "Nike Air MX Super 5000",
            image: "https://images.unsplash.com/flagged/photo-1556637640-2c80d3201be8?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8M3x8c25lYWtlcnxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=500&q=60",
            price: 249,
            oldPrice: 299,
            rating: 5.0
        },
        {
            id: 2,
            name: "Adidas UltraBoost 2025",
            image: "https://images.unsplash.com/photo-1560347416-ec9b56fdf82f?crop=entropy&cs=tinysrgb&fit=max&ixid=MnwzNjUyOXwwfDF8c2VhcmNofDJ8fGJlYWxlc3xlbnwwfHx8fDE2NTYwMzcwNzI&ixlib=rb-1.2.1&q=80&w=400",
            price: 199,
            oldPrice: 249,
            rating: 4.5
        },
        // Add more products as needed
    ];

    return (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            {products.map(product => (
                <div key={product.id} className="relative m-10 w-full max-w-xs overflow-hidden rounded-lg bg-white shadow-md">
                    <a href="#">
                        <img className="h-60 rounded-t-lg object-cover"
                             src={product.image}
                             alt={product.name} />
                    </a>
                    <span
                        className="absolute top-0 left-0 w-28 translate-y-4 -translate-x-6 -rotate-45 bg-black text-center text-sm text-white">Sale</span>
                    <div className="mt-4 px-5 pb-5">
                        <a href="#">
                            <h5 className="text-xl font-semibold tracking-tight text-slate-900">{product.name}</h5>
                        </a>
                        <div className="mt-2.5 mb-5 flex items-center">
                            <span className="mr-2 rounded bg-yellow-200 px-2.5 py-0.5 text-xs font-semibold">{product.rating}</span>
                            {/* Add the rating icons here */}
                        </div>
                        <div className="flex items-center justify-between">
                            <p>
                                <span className="text-3xl font-bold text-slate-900">${product.price}</span>
                                <span className="text-sm text-slate-900 line-through">${product.oldPrice}</span>
                            </p>
                            <a href="#"
                               className="flex items-center rounded-md bg-slate-900 px-5 py-2.5 text-center text-sm font-medium text-white hover:bg-gray-700 focus:outline-none focus:ring-4 focus:ring-blue-300">
                                <svg xmlns="http://www.w3.org/2000/svg" className="mr-2 h-6 w-6" fill="none" viewBox="0 0 24 24"
                                     stroke="currentColor" stroke-width="2">
                                    <path stroke-linecap="round" stroke-linejoin="round"
                                          d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z"/>
                                </svg>
                                Add to cart
                            </a>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Menu;
