import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import './Home.css'; // You can create specific styles for Home page if needed

function Home() {
    // Array of background image classes
    const backgroundClasses = [
        'hero-bg-1',
        'hero-bg-2',
        'hero-bg-3',
    ];

    // Set the initial background index
    const [currentBackground, setCurrentBackground] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentBackground((prevIndex) => (prevIndex + 1) % backgroundClasses.length);
        }, 3000); // Change every 3 seconds

        // Cleanup interval when component unmounts
        return () => clearInterval(interval);
    }, []);

    return (
        <div className="flex flex-col min-h-screen">
            {/* Full-Screen Hero Section */}
            <section className={`hero-section ${backgroundClasses[currentBackground]}`}>
                <h1 className="hero-title text-white text-3xl sm:text-4xl md:text-5xl lg:text-6xl xl:text-7xl font-bold mb-6 px-4 sm:px-0 leading-snug sm:leading-normal">
                    Welcome to Little Lemon
                </h1>
                <button
                    type="submit"
                    className="px-4 py-2 sm:px-6 sm:py-3 bg-yellow-500 text-white text-base sm:text-xl font-semibold rounded-lg shadow-lg hover:bg-yellow-400 transition duration-300"
                >
                    Book Now!
                </button>
            </section>

            {/* Main Content */}
            <main className="main-content">
                {/* About Us Section */}
                <div className="p-4 text-center">
                    <h2 className="text-xl sm:text-2xl md:text-3xl lg:text-4xl font-bold text-gray-800 text-center">
                        About Us
                    </h2>
                    <p className="text-sm sm:text-base md:text-lg lg:text-xl text-gray-500 mt-4 sm:mt-6 px-4 sm:px-8 text-center">
                        At Little Lemon, weâ€™re passionate about creating memorable dining experiences for you and your
                        loved ones. Located in the heart of Chicago, our restaurant brings together a vibrant
                        atmosphere, exceptional service, and a menu that celebrates flavors from around the globe.
                        {/* Add the rest of the About Us content here */}
                    </p>
                </div>

                {/* Reviews Section */}
                <section className="pb-12 mx-auto md:pb-20 max-w-7xl">
                    <div className="py-4 text-center md:py-8">
                        <h4 className="text-base font-bold tracking-wide text-center uppercase text-teal-600">Reviews</h4>
                        <p className="mt-2 tracking-tight text-gray-900 text:xl md:text-2xl">We have some fans.</p>
                    </div>

                    <div className="gap-8 space-y-8 md:columns-2 lg:columns-3">
                        {/* Add all the review components here */}
                    </div>
                </section>
            </main>
        </div>
    );
}

export default Home;
