import React, { useState } from "react";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';  // Import useNavigate for routing

const Login = () => {
    const navigate = useNavigate();  // Initialize useNavigate

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [rememberMe, setRememberMe] = useState(false);
    const [error, setError] = useState("");

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        const loginData = { username: email, password: password };

        try {
            const response = await axios.post("http://localhost:5001/login-credentials", loginData);

            if (response.status === 201) {
                // Save authentication token (or any other data) in localStorage
                localStorage.setItem("authToken", response.data.token);  // Assuming a token is returned
                // Redirect to Menu page
                navigate("/menu");
            }
        } catch (error) {
            console.error("Login error:", error.response ? error.response.data : error);
            setError("Invalid credentials, please try again.");
        }
    };

    return (
        <div className="font-[sans-serif]">
            <div className="min-h-screen flex flex-col items-center justify-center py-6 px-4">
                <div className="grid md:grid-cols-2 items-center gap-10 max-w-6xl max-md:max-w-md w-full">
                    <div>
                        <h2 className="lg:text-5xl text-3xl font-extrabold lg:leading-[55px] text-gray-800">
                            Login for Exclusive Dining Experience at Little Lemon Restaurant
                        </h2>
                        <p className="text-sm mt-6 text-gray-800">
                            Enjoy a hassle-free login process to easily make reservations and access your exclusive
                            dining experience at Little Lemon Restaurant. Let us serve you with the finest flavors.
                        </p>
                        <p className="text-sm mt-12 text-gray-800">Don't have an account <a href="javascript:void(0);"
                                                                                            className="text-blue-600 font-semibold hover:underline ml-1">Register
                            here</a></p>
                    </div>

                    <form onSubmit={handleSubmit} className="max-w-md md:ml-auto w-full">
                        <h3 className="text-gray-800 text-3xl font-extrabold mb-8">
                            Sign in
                        </h3>

                        {error && <p className="text-red-500 mb-4">{error}</p>}  {/* Error message */}

                        <div className="space-y-4">
                            <div>
                                <input name="email" type="email" autoComplete="email" required
                                       className="bg-gray-100 w-full text-sm text-gray-800 px-4 py-3.5 rounded-md outline-blue-600 focus:bg-transparent"
                                       placeholder="Email address"
                                       value={email}
                                       onChange={(e) => setEmail(e.target.value)} />
                            </div>
                            <div>
                                <input name="password" type="password" autoComplete="current-password" required
                                       className="bg-gray-100 w-full text-sm text-gray-800 px-4 py-3.5 rounded-md outline-blue-600 focus:bg-transparent"
                                       placeholder="Password"
                                       value={password}
                                       onChange={(e) => setPassword(e.target.value)} />
                            </div>
                            <div className="flex flex-wrap items-center justify-between gap-4">
                                <div className="flex items-center">
                                    <input id="remember-me" name="remember-me" type="checkbox"
                                           className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                                           checked={rememberMe}
                                           onChange={(e) => setRememberMe(e.target.checked)} />
                                    <label htmlFor="remember-me" className="ml-3 block text-sm text-gray-800">
                                        Remember me
                                    </label>
                                </div>
                                <div className="text-sm">
                                    <a href="javascript:void(0);"
                                       className="text-blue-600 hover:text-blue-500 font-semibold">
                                        Forgot your password?
                                    </a>
                                </div>
                            </div>
                        </div>

                        <div className="!mt-8">
                            <button type="submit"
                                    className="w-full shadow-xl py-2.5 px-4 text-sm font-semibold rounded text-white bg-blue-600 hover:bg-blue-700 focus:outline-none">
                                Log in
                            </button>
                        </div>

                        <div className="my-4 flex items-center gap-4">
                            <hr className="w-full border-gray-300"/>
                            <p className="text-sm text-gray-800 text-center">or</p>
                            <hr className="w-full border-gray-300"/>
                        </div>

                        <div className="space-x-6 flex justify-center">
                            <button type="button"
                                    className="border-none outline-none">
                                <svg xmlns="http://www.w3.org/2000/svg" width="32px" viewBox="0 0 512 512">
                                    <path fill="#fbbd00"
                                          d="M120 256c0-25.367 6.989-49.13 19.131-69.477v-86.308H52.823C18.568 144.703 0 198.922 0 256s18.568 111.297 52.823 155.785h86.308v-86.308C126.989 305.13 120 281.367 120 256z"
                                          data-original="#fbbd00"/>
                                    <path fill="#0f9d58"
                                          d="m256 392-60 60 60 60c57.079 0 111.297-18.568 155.785-52.823v-86.216h-86.216C305.044 385.147 281.181 392 256 392z"
                                          data-original="#0f9d58"/>
                                    <path fill="#31aa52"
                                          d="m139.131 325.477-86.308 86.308a260.085 260.085 0 0 0 22.158 25.235C123.333 485.371 187.62 512 256 512V392c-49.624 0-93.117-26.72-116.869-66.523z"
                                          data-original="#31aa52"/>
                                    <path fill="#3c79e6"
                                          d="M512 256a258.24 258.24 0 0 0-4.192-46.377l-2.251-12.299H256v120h121.452a135.385 135.385 0 0 1-51.884 55.638l86.216 86.216a260.085 260.085 0 0 0 25.235-22.158C485.371 388.667 512 324.38 512 256z"
                                          data-original="#3c79e6"/>
                                    <path fill="#cf2d48"
                                          d="m352.167 159.833 10.606 10.606 84.853-84.852-10.606-10.606C388.668 26.629 324.381 0 256 0l-60 60 60 60c36.326 0 70.479 14.146 96.167 39.833z"
                                          data-original="#cf2d48"/>
                                    <path fill="#eb4132"
                                          d="M256 120V0C187.62 0 123.333 26.629 74.98 74.98a259.849 259.849 0 0 0-22.158 25.235l86.308 86.308C162.883 146.72 206.376 120 256 120z"
                                          data-original="#eb4132"/>
                                </svg>
                            </button>
                            <button type="button"
                                    className="border-none outline-none">
                                <svg xmlns="http://www.w3.org/2000/svg" width="32px" viewBox="0 0 512 512">
                                    <path fill="#1877f2"
                                          d="M512 256c0 127.78-93.62 233.69-216 252.89V330h59.65L367 256h-71v-48.02c0-20.25 9.92-39.98 41.72-39.98H370v-63s-29.3-5-57.31-5c-58.47 0-96.69 35.44-96.69 99.6V256h-65v74h65v178.89C93.62 489.69 0 383.78 0 256 0 114.62 114.62 0 256 0s256 114.62 256 256z"
                                          data-original="#1877f2"/>
                                    <path fill="#fff"
                                          d="M355.65 330 367 256h-71v-48.021c0-20.245 9.918-39.979 41.719-39.979H370v-63s-29.296-5-57.305-5C254.219 100 216 135.44 216 199.6V256h-65v74h65v178.889c13.034 2.045 26.392 3.111 40 3.111s26.966-1.066 40-3.111V330z"
                                          data-original="#ffffff"/>
                                </svg>
                            </button>
                            <button type="button"
                                    className="border-none outline-none">
                                <svg xmlns="http://www.w3.org/2000/svg" width="32px" viewBox="0 0 22.773 22.773">
                                    <path
                                        d="M15.769 0h.162c.13 1.606-.483 2.806-1.228 3.675-.731.863-1.732 1.7-3.351 1.573-.108-1.583.506-2.694 1.25-3.561C13.292.879 14.557.16 15.769 0z"
                                        fill="#1d1d1b"/>
                                    <path
                                        d="M8.844 7.617c-.498-.77-.412-2.003.208-2.598 2.221-2.06 4.947-1.097 6.33 1.163 2.056 2.526.33 6.038-2.745 6.238-.787.146-1.728-.526-2.118-1.173-1.173.797-1.876 2.08-1.198 3.153 1.745 2.46-1.505 5.747-4.164 3.38-1.497 1.25-2.768-3.005-1.678-4.162-.086-2.39 1.1-3.85-1.398-5.146z"
                                        fill="#ffad6d"/>
                                </svg>
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
