import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
    const navigate = useNavigate();

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [rememberMe, setRememberMe] = useState(false);
    const [error, setError] = useState("");  // To store any error message

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        const loginData = { username: email, password: password };

        try {
            // Make a GET request to your login credentials API with query params
            const response = await axios.get(
                `http://localhost:5001/login-credentials?username=${loginData.username}&password=${loginData.password}`
            );

            if (response.status === 200) {
                // If successful, save token or data as necessary (could use JWT for token)
                localStorage.setItem("authToken", response.data.token); // Replace with your actual token

                // Redirect to /menu page after successful login
                navigate("/menu");
            }
        } catch (error) {
            console.error("Login error:", error.response ? error.response.data : error);
            setError("Invalid credentials, please try again.");  // Set error message
        }
    };

    return (
        <div className="font-[sans-serif]">
            <div className="min-h-screen flex flex-col items-center justify-center py-6 px-4">
                <div className="grid md:grid-cols-2 items-center gap-10 max-w-6xl max-md:max-w-md w-full">
                    <div>
                        <h2 className="lg:text-5xl text-3xl font-extrabold lg:leading-[55px] text-gray-800">
                            Login for Exclusive Dining Experience at Little Lemon Restaurant
                        </h2>
                        <p className="text-sm mt-6 text-gray-800">
                            Enjoy a hassle-free login process to easily make reservations and access your exclusive
                            dining experience at Little Lemon Restaurant. Let us serve you with the finest flavors.
                        </p>
                        <p className="text-sm mt-12 text-gray-800">
                            Don't have an account <a href="javascript:void(0);" className="text-blue-600 font-semibold hover:underline ml-1">
                            Register here
                        </a>
                        </p>
                    </div>

                    <form onSubmit={handleSubmit} className="max-w-md md:ml-auto w-full">
                        <h3 className="text-gray-800 text-3xl font-extrabold mb-8">
                            Sign in
                        </h3>

                        {error && <p className="text-red-500 mb-4">{error}</p>}  {/* Display error if any */}

                        <div className="space-y-4">
                            <div>
                                <input
                                    name="email"
                                    type="email"
                                    autoComplete="email"
                                    required
                                    className="bg-gray-100 w-full text-sm text-gray-800 px-4 py-3.5 rounded-md outline-blue-600 focus:bg-transparent"
                                    placeholder="Email address"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                />
                            </div>
                            <div>
                                <input
                                    name="password"
                                    type="password"
                                    autoComplete="current-password"
                                    required
                                    className="bg-gray-100 w-full text-sm text-gray-800 px-4 py-3.5 rounded-md outline-blue-600 focus:bg-transparent"
                                    placeholder="Password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                />
                            </div>
                            <div className="flex flex-wrap items-center justify-between gap-4">
                                <div className="flex items-center">
                                    <input
                                        id="remember-me"
                                        name="remember-me"
                                        type="checkbox"
                                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                                        checked={rememberMe}
                                        onChange={(e) => setRememberMe(e.target.checked)}
                                    />
                                    <label htmlFor="remember-me" className="ml-2 text-sm text-gray-800">Remember me</label>
                                </div>
                                <div>
                                    <button
                                        type="submit"
                                        className="w-full bg-blue-600 text-white py-3.5 rounded-md focus:outline-none"
                                    >
                                        Sign In
                                    </button>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
